<?phpini_set('memory_limit', '-1');ini_set('max_execution_time', 90);
function debug($value, $label = null)
{// 	echo '<pre>';// 	print_r($value);// 	echo '</pre>';// 	return;// 	die;
	$label = jb_get_tracelog(debug_backtrace(), $label);
	echo getdebug($value, $label);
}function write_log($log_file, $error, $type = E_USER_NOTICE){	date_default_timezone_set('Asia/Ho_Chi_Minh');	$date = date('d/m/Y H:i:s');	$error = $date.": ".$error."\n";	$log_file = JPATH_ROOT."/logs/".$log_file;	if(!file_exists($log_file) || filesize($log_file) > 1048576){		$fh = fopen($log_file, 'w');	}	else{		//echo "Append log to log file ".$log_file;		$fh = fopen($log_file, 'a');	}	fwrite($fh, $error);	fclose($fh);}
	function getdebug($value, $label = null)		{			$value = htmlentities(print_r($value, true));			return "<pre>$label$value</pre>";		}				function trace()		{			$trace = debug_backtrace();			foreach ($trace as $k => $t)			{				if (!isset($_GET['object'])) $trace[$k]['object'] = null;				if (!isset($_GET['args'])) $trace[$k]['args'] = null;			}			debug($trace);			jb_exit_app();		}				function using($class)		{			if (!including(FileHandler::GetNameFromClass($class)))				throw new UsingFailedException($class);		}				function including($file_name)		{			try			{				$path = FileHandler::GetUsingPath($file_name);				if ($path)				{					require_once($path);					return true;				}				else					return jb_require_file(AppConfig::GetInstance()->USING_DIRS, $file_name);			}			catch (Exception $ex)			{				debug('Uncaught exception: '.$ex->getMessage()."\n".$ex->getTraceAsString());				return false;			}		}				function jb_require_file($paths, $file_name)		{			foreach($paths as $path)			{				$file = $path.DS.$file_name;				if (file_exists($file))				{					require_once($file);					FileHandler::SaveUsingPath($file_name, $file);					return true;				}				else if (file_exists($path) && is_dir($path))				{					$subs = FileHandler::GetSubDirs($path, true);					if (!empty($subs))						if (jb_require_file($subs, $file_name))							return true;				}			}			return false;		}				function jb__autoload($class)		{			using($class);		}				function jb_console($var, $label = null)		{			$label = jb_get_tracelog(debug_backtrace(), $label);			Log::LogVariable($var, $label);		}				function jb_get_tracelog($trace, $label = null)		{		$line = $trace[0]['line'];			$file = jb_is_set($trace[1]['file']);			$func = $trace[1]['function'];			$class = jb_is_set($trace[1]['class']);			$log = "<span style='color:#FF3300'>-- $file - line:$line - $class-$func()</span><br/>";			if($label)				$log .= "<span style='color:#FF99CC'>$label</span> ";				return $log;		}				function jb_is_set(&$var, $substitute = null)		{			return isset($var) ? $var : $substitute;		}				function jb_exit_app($status = null)		{			MysqlAdapter::CloseAll();			exit($status);		}				function jb_dump($value, $label = null)		{			$label = jb_get_tracelog(debug_backtrace(), $label);			$value = htmlentities(var_export($value, true));			echo "<pre>$label$value</pre>";		}				function jb_array_get($array, $key)		{			return isset($array[$key]) ? $array[$key] : null;		}				
